import React, { useState, useEffect, useContext } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { AuthContext } from '../contexts/AuthContext';
import './Header.css';

const Header = ({ onLocationChange }) => {
  const { user, logout } = useContext(AuthContext);
  const [selectedCity, setSelectedCity] = useState('Select Location');
  const [showLocationDropdown, setShowLocationDropdown] = useState(false);
  const [searchTerm, setSearchTerm] = useState('');
  const navigate = useNavigate();

  const indianCities = [
    'Mumbai', 'Delhi', 'Bengaluru', 'Hyderabad', 'Chennai', 'Pune', 'Kolkata',
    'Ahmedabad', 'Jaipur', 'Chandigarh', 'Lucknow', 'Indore', 'Surat',
    'Coimbatore', 'Bhopal', 'Nagpur', 'Kochi', 'Patna', 'Guwahati'
  ];

  useEffect(() => {
    const token = localStorage.getItem('token');
    const userData = localStorage.getItem('user');
    const savedCity = localStorage.getItem('selectedCity');

    if (token && userData) {
      setUser(JSON.parse(userData));
    } else {
      // Check if user is logged in via AuthContext (if available)
      const authUser = localStorage.getItem('user');
      if (authUser) {
        setUser(JSON.parse(authUser));
      }
    }

    if (savedCity) {
      setSelectedCity(savedCity);
    }
  }, []);

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    setUser(null);
    alert('Logged out successfully!');
    navigate('/');
  };

  const handleCitySelect = (city) => {
    setSelectedCity(city);
    localStorage.setItem('selectedCity', city);
    setShowLocationDropdown(false);
    setSearchTerm('');

    // Dispatch custom event to notify other components about city change
    window.dispatchEvent(new CustomEvent('cityChanged', { detail: city }));
  };

  const handleCurrentLocation = () => {
    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition(
        async (position) => {
          try {
            // Use a reverse geocoding service to get city name
            const response = await fetch(
              `https://api.bigdatacloud.net/data/reverse-geocode-client?latitude=${position.coords.latitude}&longitude=${position.coords.longitude}&localityLanguage=en`
            );
            const data = await response.json();

            if (data.city) {
              handleCitySelect(data.city);
            } else {
              alert('Could not determine your city. Please select manually.');
            }
          } catch (error) {
            console.error('Error getting location:', error);
            alert('Could not determine your city. Please select manually.');
          }
        },
        (error) => {
          console.error('Geolocation error:', error);
          alert('Location access denied. Please select your city manually.');
        }
      );
    } else {
      alert('Geolocation is not supported by this browser.');
    }
  };

  const filteredCities = indianCities.filter(city =>
    city.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <header className="header">
      <div className="container">
        <Link to="/" className="logo">
          <h1>RentLONA.com</h1>
        </Link>

        <div className="location-selector">
          <button
            className="location-btn"
            onClick={() => setShowLocationDropdown(!showLocationDropdown)}
            title="Change Location"
          >
            <span className="location-icon">üìç</span>
            <span className="location-text">{selectedCity}</span>
            <span className="dropdown-arrow">‚ñº</span>
          </button>

          {showLocationDropdown && (
            <>
              <div
                className="location-dropdown-overlay"
                onClick={() => setShowLocationDropdown(false)}
              ></div>
              <div className="location-dropdown">
                <div className="dropdown-header">
                  <h3>Select Your City</h3>
                  <button
                    className="close-btn"
                    onClick={() => setShowLocationDropdown(false)}
                  >
                    ‚úï
                  </button>
                </div>

                <div className="dropdown-content">
                  <div className="location-search">
                    <input
                      type="text"
                      placeholder="Search for a city..."
                      value={searchTerm}
                      onChange={(e) => setSearchTerm(e.target.value)}
                    />
                  </div>

                  <button
                    className="current-location-btn"
                    onClick={handleCurrentLocation}
                  >
                    <span className="btn-icon">üìç</span>
                    <span className="btn-text">Use Current Location</span>
                  </button>

                  <div className="cities-section">
                    <h4>Popular Cities</h4>
                    <div className="cities-grid">
                      {indianCities.slice(0, 12).map(city => (
                        <button
                          key={city}
                          className="city-btn"
                          onClick={() => handleCitySelect(city)}
                        >
                          {city}
                        </button>
                      ))}
                    </div>
                  </div>

                  {searchTerm && (
                    <div className="search-results">
                      <h4>Search Results</h4>
                      <div className="cities-list">
                        {filteredCities.slice(0, 8).map(city => (
                          <button
                            key={city}
                            className="city-option"
                            onClick={() => handleCitySelect(city)}
                          >
                            {city}
                          </button>
                        ))}
                      </div>
                    </div>
                  )}

                  <div className="dropdown-footer">
                    <button
                      className="all-india-btn"
                      onClick={() => handleCitySelect('All India')}
                    >
                      <span className="btn-icon">üåç</span>
                      <span className="btn-text">All India</span>
                    </button>
                  </div>
                </div>
              </div>
            </>
          )}
        </div>

        <nav className="nav">
          <Link to="/listings">Browse</Link>
          <Link to="/dashboard" className="btn btn-primary">
            Dashboard
          </Link>
          {user ? (
            <>
              <Link to="/create-listing" className="btn btn-primary">
                Post Listings
              </Link>
              <Link to="/messages">Messages</Link>
              <Link to={`/profile/${user.id}`}>Profile</Link>
              <button onClick={handleLogout} className="btn btn-secondary">
                Logout
              </button>
            </>
          ) : (
            <>
              <Link to="/login">Login</Link>
              <Link to="/register" className="btn btn-primary">
                Sign Up
              </Link>
            </>
          )}
        </nav>
      </div>
    </header>
  );
};

export default Header;
