import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import './App.css';

// Components
import Header from './components/Header';
import Footer from './components/Footer';
import LocationModal from './components/LocationModal';

// Pages
import Home from './pages/Home';
import Login from './pages/Login';
import Register from './pages/Register';
import Listings from './pages/Listings';
import ListingDetail from './pages/ListingDetail';
import CreateListing from './pages/CreateListing';
import Dashboard from './pages/Dashboard';
import Messages from './pages/Messages';
import Profile from './pages/Profile';

function App() {
  const [selectedCity, setSelectedCity] = useState(null);
  const [showLocationModal, setShowLocationModal] = useState(false);

  useEffect(() => {
    const savedCity = localStorage.getItem('selectedCity');
    if (savedCity) {
      setSelectedCity(savedCity);
    } else {
      setShowLocationModal(true);
    }
  }, []);

  const handleLocationSelect = (city) => {
    setSelectedCity(city);
    localStorage.setItem('selectedCity', city);
    setShowLocationModal(false);

    // Dispatch custom event to notify other components about city change
    window.dispatchEvent(new CustomEvent('cityChanged', { detail: city }));
  };

  const handleLocationChange = () => {
    setShowLocationModal(true);
  };

  return (
    <Router>
      <div className="App">
        {selectedCity && <Header onLocationChange={handleLocationChange} />}
        <main className="main-content">
          {selectedCity ? (
            <Routes>
              <Route path="/" element={<Home />} />
              <Route path="/login" element={<Login />} />
              <Route path="/register" element={<Register />} />
              <Route path="/listings" element={<Listings />} />
              <Route path="/listings/:id" element={<ListingDetail />} />
              <Route path="/create-listing" element={<CreateListing />} />
              <Route path="/dashboard" element={<Dashboard />} />
              <Route path="/messages" element={<Messages />} />
              <Route path="/profile/:id" element={<Profile />} />
            </Routes>
          ) : (
            <div className="location-required">
              <div className="location-message">
                <h2>üìç Please select your location</h2>
                <p>Select your city to explore rental listings near you</p>
              </div>
            </div>
          )}
        </main>
        {selectedCity && <Footer />}

        {showLocationModal && (
          <LocationModal onLocationSelect={handleLocationSelect} />
        )}
      </div>
    </Router>
  );
}

export default App;
