import React, { useState, useEffect, useContext } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { AuthContext } from '../contexts/AuthContext';
import LocationModal from './LocationModal';
import './Header.css';

const Header = ({ onLocationChange }) => {
  const { user, logout } = useContext(AuthContext);
  const [selectedCity, setSelectedCity] = useState('Select Location');
  const [showLocationModal, setShowLocationModal] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    const savedCity = localStorage.getItem('selectedCity');
    if (savedCity) {
      setSelectedCity(savedCity);
    }
  }, []);

  const handleLogout = () => {
    logout();
    navigate('/');
  };

  const handleLocationSelect = (city) => {
    setSelectedCity(city);
    localStorage.setItem('selectedCity', city);
    setShowLocationModal(false);

    // Dispatch custom event to notify other components about city change
    window.dispatchEvent(new CustomEvent('cityChanged', { detail: city }));
  };

  return (
    <header className="header">
      <div className="container">
        <Link to="/" className="logo">
          <h1>RentLONA.com</h1>
        </Link>

        <div className="location-selector">
          <button
            className="flex items-center bg-white/10 backdrop-blur-sm border border-white/20 rounded-full px-4 py-2 text-white hover:bg-white/20 transition-all duration-300 shadow-lg"
            onClick={() => setShowLocationDropdown(!showLocationDropdown)}
            title="Change Location"
          >
            <span className="text-lg mr-2">üìç</span>
            <span className="font-medium text-sm">{selectedCity}</span>
            <span className="ml-2 text-xs transform transition-transform duration-300" style={{transform: showLocationDropdown ? 'rotate(180deg)' : 'rotate(0deg)'}}>‚ñº</span>
          </button>

          {showLocationDropdown && (
            <>
              <div
                className="location-dropdown-overlay"
                onClick={() => setShowLocationDropdown(false)}
              ></div>
              <div className="location-dropdown">
                <div className="dropdown-header">
                  <h3>Select Your City</h3>
                  <button
                    className="close-btn"
                    onClick={() => setShowLocationDropdown(false)}
                  >
                    ‚úï
                  </button>
                </div>

                <div className="dropdown-content">
                  <button
                    className="current-location-btn"
                    onClick={handleCurrentLocation}
                  >
                    <span className="btn-icon">üìç</span>
                    <span className="btn-text">Use Current Location</span>
                  </button>

                  <div className="cities-section">
                    <h4>Popular Cities</h4>
                    <div className="cities-grid">
                      {indianCities.slice(0, 12).map(city => (
                        <button
                          key={city}
                          className="city-btn"
                          onClick={() => handleCitySelect(city)}
                        >
                          {city}
                        </button>
                      ))}
                    </div>
                  </div>

                  {searchTerm && (
                    <div className="search-results">
                      <h4>Search Results</h4>
                      <div className="cities-list">
                        {filteredCities.slice(0, 8).map(city => (
                          <button
                            key={city}
                            className="city-option"
                            onClick={() => handleCitySelect(city)}
                          >
                            {city}
                          </button>
                        ))}
                      </div>
                    </div>
                  )}

                  <div className="dropdown-footer">
                    <button
                      className="all-india-btn"
                      onClick={() => handleCitySelect('All India')}
                    >
                      <span className="btn-icon">üåç</span>
                      <span className="btn-text">All India</span>
                    </button>
                  </div>
                </div>
              </div>
            </>
          )}
        </div>

        <nav className="nav">
          <Link to="/listings">Browse</Link>
          <Link to="/dashboard" className="btn btn-primary">
            Dashboard
          </Link>
          {user ? (
            <>
              <Link to="/create-listing" className="btn btn-primary">
                Post Listings
              </Link>
              <Link to="/messages">Messages</Link>
              <button onClick={handleLogout} className="btn btn-secondary">
                Logout
              </button>
            </>
          ) : (
            <>
              <Link to="/login">Login</Link>
              <Link to="/register" className="btn btn-primary">
                Sign Up
              </Link>
            </>
          )}
        </nav>
      </div>
    </header>
  );
};

export default Header;
