import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import './Home.css';

const Home = () => {
  const [searchTerm, setSearchTerm] = useState('');
  const navigate = useNavigate();

  const handleSearch = (e) => {
    e.preventDefault();
    if (searchTerm.trim()) {
      navigate(`/listings?search=${encodeURIComponent(searchTerm.trim())}`);
    } else {
      navigate('/listings');
    }
  };

  return (
    <div className="home">
      {/* Hero Section */}
      <section className="hero-section">
        <div className="hero-content">
          <h1>RentLONA</h1>
          <p>Find anything you need to rent</p>

          <form className="search-bar" onSubmit={handleSearch}>
            <input
              type="text"
              placeholder="Search for rentals..."
              className="search-input"
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
            />
            <button type="submit" className="search-btn">Search</button>
          </form>

          <div className="hero-buttons">
            <Link to="/listings" className="btn btn-primary">Browse Listings</Link>
            <Link to="/create-listing" className="btn btn-secondary">List Your Item</Link>
          </div>
        </div>
      </section>

      {/* Categories Section */}
      <section className="categories-section">
        <div className="container">
          <h2>Popular Categories</h2>
          <div className="categories-grid">
            <Link to="/listings?category=property" className="category-card">
              <span className="category-icon">üè†</span>
              <span className="category-name">Property</span>
            </Link>
            <Link to="/listings?category=vehicles" className="category-card">
              <span className="category-icon">üöó</span>
              <span className="category-name">Vehicles</span>
            </Link>
            <Link to="/listings?category=electronics" className="category-card">
              <span className="category-icon">üíª</span>
              <span className="category-name">Electronics</span>
            </Link>
            <Link to="/listings?category=furniture" className="category-card">
              <span className="category-icon">ü™ë</span>
              <span className="category-name">Furniture</span>
            </Link>
          </div>
        </div>
      </section>
    </div>
  );
};

export default Home;
